  const [data, getData] = useState();
  const [inputText, setInputText] = useState("");
  const favourited = "";

  const getApiData = async () => {
    const { data } = await axios.get(
      `https://thesimpsonsquoteapi.glitch.me/quotes?count=50`
    );
    getData(data);
  };
  useEffect(() => {
    getApiData();
  }, []); // Only runs once
