import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import Simpsons from "./Components/Simpsons";

class App extends Component {
  state = { totalFavourites: 0 };

  componentDidMount() {
    this.getSimpsons();
  }

  getSimpsons = async () => {
    const { data } = await axios.get(
      `https://thesimpsonsquoteapi.glitch.me/quotes?count=50`
    );
    data.forEach((e, index) => {
      e.id = index + 1;
    });
    this.setState({ simpsons: data });
  };

  favourited = (id) => {
    this.setState({ totalFavourites: this.state.totalFavourites + 1 });
    const simpsonsCopy = [...this.state.simpsons];

    const which = simpsonsCopy.findIndex((item) => {
      return item.id === id;
    });
    simpsonsCopy[which + 1].favourite = !simpsonsCopy[which + 1].favourite;
    console.log(which + 1, "WHICH");
    this.setState({ simpsons: simpsonsCopy });
  };

  render() {
    const { simpsons, totalFavourites } = this.state;
    if (!simpsons) {
      return <p>Loading...</p>;
    }

    return (
      <>
        <p>Total Favourited: {totalFavourites}</p>
        <Simpsons simpsons={simpsons} favourited={this.favourited} />
      </>
    );
  }
}

export default App;
