import React from "react";
import Character from "./Character";
import Content from "./Content";

function Simpson(id, item, favourited) {
  const { character, characterDirection, image, quote, favourite } = item;
  const onClick = (e) => {
    favourited(e.target.id);
  };

  return (
    <div className={`mainCard ${favourite ? "fav" : ""}`} onClick={onClick}>
      <Character name={character} />
      <Content image={image} direction={characterDirection} quote={quote} />
    </div>
  );
}

export default Simpson;
