import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import Simpsons from "./Components/Simpsons";

class App extends Component {
  state = { totalFavourites: 0 };

  componentDidMount() {
    this.getSimpsons();
  }

  getSimpsons = async () => {
    const { data } = await axios.get(
      `https://thesimpsonsquoteapi.glitch.me/quotes?count=50`
    );
    this.setState({ simpsons: data });
  };

  favourited = () => {
    this.setState({ totalFavourites: this.state.totalFavourites++ });
    console.log(this.state.totalFavourites);
  };

  render() {
    const { simpsons, totalFavourites } = this.state;
    console.log(simpsons);
    if (!simpsons) {
      return <p>Loading...</p>;
    }

    return (
      <>
        <p>Total Favourited: {totalFavourites}</p>
        <Simpsons simpsons={simpsons} favourited={this.favourited} />
      </>
    );
  }
}

export default App;
